function [h, f] = brain_montage(V, range, smooth, titlen, fname, cmap, cbar_units, mont_size)

% V is a 3D matrix read in using read_avw.m (FSL)
% range is the [min max] colorscale
% Smooth Off:On = 0:1 
% disp_slices .... which slices to be displayed
% mont_size ... 
% fname is the title displayed in the window bar
% what type of colormap to use

[row, col, slice] = size(V);
montage_V = zeros(col,row,1,slice); 

f = figure('name',fname);

if smooth == 0
    % no smooth
    myfilter = fspecial('gaussian',[1 1], 1.0);
elseif smooth == 1 
    % smooth
    myfilter = fspecial('gaussian',[3 3], 0.6);  
end

for n = 1:slice 

    filter_IM = imfilter(V(:,:,n), myfilter, 'replicate');
    montage_V(:,:,1,n) =  rot90(filter_IM, 1);
    %montage_V(:,:,1,n) =  rot90(filter_IM, 3);
    %size(rot90(V(:,:,n), 1))
    %montage_V(:,:,1,n) = rot90(V(:,:,n), 1);
    
end

disp_slices = [1:slice]; %could replace this with function input
%mont_size = [3 ceil(slice/3)]; %could replace this with function input
%mont_size = [1 1]; %could replace this with function input

%disp_slices = [3:5]; %could replace this with function input
%mont_size = [1 3]; %could replace this with function input

%disp_slices = [46:65]; %could replace this with function input
%mont_size = [1 5]; %could replace this with function input
%mont_size = [11 5];
%hold on, montage(montage_V(:,:,1,:),'displayrange', range, 'size', [10 10]) ;
hold on, h = montage(montage_V(:,:,1,disp_slices),'displayrange', range, 'size', mont_size) ;
%hold on, montage(montage_CMRO2(:,:,:,3:8),'displayrange', [0 1], 'size', [2 3]) ;
%imshow(montage_CMRO2(:,:,:,3),'displayrange', [0 250], 'InitialMagnification', 800) ;
